mapper.select:
select scno, sctitle , to_char(scdate, 'yyyy-mm-ddHH24:mi') scdate , sno , sname from schedule JOIN study USING(sno)
scno = #{word}
sctitle like '%' || #{word} || '%'
sno = #{word}
order by scno desc

select count(*)
from schedule

	
		
			
				scno = #{word}
			
			
				sctitle like '%' || #{word} || '%'
			
			
				sno = #{word}

SELECT e.empno, j.sno, s.SCTITLE
FROM EMPLOYEE e, join_study j, schedule s
WHERE e.empno = j.empno AND j.sno = s.SNO AND e.empno=#{empno};

mapper.insert:
insert into schedule(scno, sctitle, scdate, sno)
values(SCHEDULE_SCNO.nextval
		, #{ sctitle:VARCHAR }
		, TO_DATE(#{scdate:DATE} , 'yyyy-mm-ddHH24:mi')
		, #{ sno:INTEGER }
		)
