mapper.select:
select scno, sctitle, to_char(scdate, 'yyyy-mm-dd') scdate, sno
from schedule
where scno = #{scno}

select scno, sctitle, to_char(scdate, 'yyyy-mm-dd') scdate, sno
from board

	
		
			
				scno = #{word}
			
			
				sctitle like '%' || #{word} || '%'
			
			
				sno like #{word}
			
		
	

order by no desc
