mapper.select:
select *
from study
where sno = #{ sno }

select sno, smaster, sname, smax, scurr, cno, cname
from study
JOIN category USING(cno)

	
		
			
				cno = #{ word } 
			
			
				sname like '%'||#{ word }||'%' 
			
			
				smaster = #{ word }

select jno, empno, js.sno sno, st.sname sname
from join_study js, study st
where js.sno = st.sno(+) and empno = #{ empno };
